;(function($){
  $.extend($.fn, {
    aeonNav: function(options){

      var defaults = {
        Menulabel: '&#9776',
        MenubgColor: '#ddd',
        MenutextSide: 'right',
        MenutextColor: '#000',
        MenufontSize: '1.35em',
        NavbgColor: '#ddd'
      };// End of Deafult options

      //$('#menu').css('display','none');

      var options = $.extend(defaults, options);
        return this.each(function(){
            var aeonOptions = options;
            var obj = $(this);
        
            $(this).before('<div id="menu"> <span>'+ aeonOptions.Menulabel +'</span> </div>');

            $("#menu").css({
                'background-color' : aeonOptions.MenubgColor,
                'text-align' : aeonOptions.MenutextSide,
                'font-size' : aeonOptions.MenufontSize,
                'padding' : '5px'
            });
            
            $("#menu span").css({
                'cursor' : 'pointer',
                'margin-right' : '5px',
                'margin-left' : '5px'
            });

            var navElement = $(this);

            $("#menu").click(function(){
                $(navElement).animate({
                    height: 'toggle'
                }).css({
                    'z-index': '1000',
                    'background-color' : aeonOptions.NavbgColor,
                    
                });

            });

            /*if (document.documentElement.clientWidth < 900) {
                  $('#nav.js').css('display','none');   
             }*/

            $(window).resize(function(){ 
                if(window.innerWidth > 768) { 
                    $(navElement).removeAttr("style");
                } 
            });

        });// End of each()   
      return
    }//Options
  });//Extend jQuery Function
})(jQuery)//Function